RecMedia["a35cecde-680d-408d-9281-74305899dd20"] = {
        itemDisplayName = "RM_91e040db-5de6-4960-8375-147ee6b47347", -- VHS: KY Medical Frontline E1
	title = "RM_0c157be1-f321-44e2-8917-ab224e3f4ad6", -- KY Medical Frontline
	subtitle = "RM_c06028fe-b78a-4b24-889e-0adb806c8be8", -- Episode 1: Lacerations
	author = nil,
	extra = "RM_39b66d48-daa0-4a53-ac0f-90271a0039e9", -- Follow Dr. Monroe and staff and learn how to treat lacerations in this edition of KY Medical Frontline.
	spawning = 0,
	category = "Retail-VHS",
	lines = {
        { text = "RM_1536de5b-09ea-4fec-9c34-fc34d42e7351", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- You are watching, KY Medical Frontline!
        { text = "RM_c5c971d0-823c-4e09-a875-631c547731a2", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- The time is 14:50pm...
        { text = "RM_0d5616f5-78ac-42e8-b069-b89cb598e268", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- A young boy has fallen over while playing outside...
        { text = "RM_74e095e2-0bc0-42e0-96fb-2055815c7752", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- His mother has rushed him into A&E...
        { text = "RM_4e21db6d-bb29-41e1-8d45-2826a0aeeb8b", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Good afternoon, I'm Doctorr Monroe...
        { text = "RM_594995d6-4e63-4c81-a814-9e7af1c0ebf9", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- I'll be looking after you today.
        { text = "RM_ffd7ab95-775d-4369-81b8-e7103eb9cd71", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- And what is your name little guy?
        { text = "RM_292959d8-fcde-4158-a78b-7d8b3c236759", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Timmy
        { text = "RM_db15e1df-e477-4b7c-b6df-32c26a11bb81", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Looks like you have a nasty cut on your leg there Timmy.
        { text = "RM_e07e72b9-1e52-4824-a134-fa981ecd1712", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Let's get that sorted out!
        { text = "RM_fd13ee28-0cba-454b-9a3e-01ac59e077b7", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Don't worry, it looks like a simple laceration, that's all.
        { text = "RM_0a955928-1fc0-4225-a561-44df8628d02f", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Let's clean the area up with some disinfectant.
        { text = "RM_7e8776f9-de1d-4ba0-a14a-9dfc639b81a2", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Now this may sting a little...
        { text = "RM_7030bc29-e25a-40f9-9241-d5be57ecfb3d", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Oww...
        { text = "RM_4c0fd70e-e6b4-46a3-a5dd-141cc1568015", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Sorry there, Timmy...
        { text = "RM_67cfb0ff-3b6b-4b48-8491-6e8088224bb8", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- But it is better to clean it now before the wound becomes infected!
        { text = "RM_e63d30e5-9254-4be2-a123-d7b0e34726d9", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- An infection is much worse!
        { text = "RM_cdea6360-92a4-47ab-85ea-6173b6e8725d", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- We'll get the nurse here to apply a bandage to the area...
        { text = "RM_f66352be-fe31-495e-bbb5-8346149c547e", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Much better.
        { text = "RM_02bae59d-870c-4ad3-bb43-dd8b870446e6", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Now, the bandage should be changed when it gets dirty.
        { text = "RM_4f55cadf-0c4a-4759-b124-159ad057ef88", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Your mom can do that for you!
        { text = "RM_2d4908a3-53e7-49a7-80f3-8c21efa14bf5", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Just remove the dirty bandage and reapply a clean one.
        { text = "RM_83890572-ec4a-40e3-ab42-02d60c05b542", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Take it easy for a day or two...
        { text = "RM_0108c5d1-11e6-43c2-90c1-0feeba2ed37d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- You'll be back outside playing in no time!
        { text = "RM_275ebc0e-2908-4b1b-9cbe-627c090d5e51", r = 0.90, g = 0.90, b = 0.40, codes = "BOR-1" }, -- Say thank you to the doctor now, Timothy.
        { text = "RM_5286b54c-4c01-402e-9679-75501c37b0ff", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Thank you, doctor!
        { text = "RM_e0df9d6b-f9ad-4290-a21f-37d620b11b86", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Haha, take care now...
	},
};

RecMedia["0f0eac2a-ee9e-41c2-9622-fef5bd3991fe"] = {
	itemDisplayName = "RM_6f98888f-2e1c-4895-b465-8e97d94adee5", -- VHS: KY Medical Frontline E2
	title = "RM_0c157be1-f321-44e2-8917-ab224e3f4ad6",  -- KY Medical Frontline
	subtitle = "RM_8ff6c6be-ebb8-42b4-9ccb-515de57d0f73", -- Episode 2: Deep Wounds
	author = nil,
	extra = "RM_73d0ab21-768f-476b-8054-e8dfbc23190e", -- Follow Dr. Monroe and staff and learn how to treat a deep wound in this edition of KY Medical Frontline.
	spawning = 0,
	category = "Retail-VHS",
	lines = {
        { text = "RM_1536de5b-09ea-4fec-9c34-fc34d42e7351", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- You are watching, KY Medical Frontline!
        { text = "RM_e3f016c6-f640-4d7a-bfeb-c8d0167823bc", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- The time is 10:47am...
        { text = "RM_958edf1f-a034-417d-8f96-f4410fafbe87", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- An armed robbery gone wrong...
        { text = "RM_9b8e6828-cbe2-4458-807a-8f94120f7bfb", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- A young female is rushed in after being shot...
        { text = "RM_6905bf5a-b4ec-47e0-baea-4a7471920085", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Is the woman conscious, nurse?
        { text = "RM_1f5d52d8-6514-4d21-812e-1d4e0327a291", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Just about, Doctor Monroe, but she's in a lot of pain.
        { text = "RM_f733b08a-cbd5-4948-8cc0-20ac75548008", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Major blood loss...
        { text = "RM_73715d9f-f0be-4ae2-8b4c-1fa31824f340", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Let's assess the patient.
        { text = "RM_f93bb0ac-d29f-4b30-a608-72e76a16f149", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Two gunshot wounds to the upper chest...
        { text = "RM_265d9c66-1d2f-4b61-9b1a-bbba54b8160d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- No exit wounds...
        { text = "RM_5c428047-3633-43e6-80f0-9947c52aeb95", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Which means the bullets are still in the patient!
        { text = "RM_3f56da7f-0c17-45a2-8051-9841a9ddb219", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We should start by removing the foreign objects...
        { text = "RM_c6f31825-92d3-459f-b795-c484f2cdb9e5", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Let's get them out, shall we?
        { text = "RM_6ca6d3c8-8307-45a7-a7c4-ad41ad4b65a9", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Tweezers... Tray...
        { text = "RM_7a75f3c7-a203-4f8f-86d6-83eb0401aabb", r = 1.00, g = 1.00, b = 1.00, codes = "BOR-1" }, -- *dink*
        { text = "RM_3f5423ec-531d-4980-a441-e2bf05b2799c", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- One bullet removed!
        { text = "RM_7a75f3c7-a203-4f8f-86d6-83eb0401aabb", r = 1.00, g = 1.00, b = 1.00, codes = "BOR-1" }, -- *dink*
        { text = "RM_f161b3cc-d936-486a-a735-2f64249478e3", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- And there's the other!
        { text = "RM_62a808f6-37d2-4f2a-ae94-89758c350a2d", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- And to finish up - some disinfectant.
        { text = "RM_c0fbfdfb-c9ec-4a96-8782-65d0d998acb4", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Patient's looking pale doc...
        { text = "RM_8d7ecbab-c0e8-4843-87a1-6a59964faf0d", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Maybe we should start to close her up?
        { text = "RM_0c345731-1411-4efb-8312-a529961f3351", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Couldn't agree more...
        { text = "RM_0fa9366e-13f4-4467-aba6-5304a7eb4188", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Suture needle, thread, and holder?
        { text = "RM_cafe1bd5-17ab-4442-b7d2-cfc77355bb93", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Right here doc!
        { text = "RM_753872df-fa43-462b-9d46-4d01facd063f", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Steady, no need to rush...
        { text = "RM_61ec5432-fe2b-4583-b1ba-f5977f03b01d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- All done, let's cover this up with a bandage.
        { text = "RM_85728c57-627d-4379-9395-665066ec4776", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We can move her to intensive care.
        { text = "RM_6ad07667-7ef9-4cc5-9c62-73f28a51bbfb", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- This will take time to heal.
        { text = "RM_35a05ac0-148a-44f3-ac24-e93edc010416", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Nurse, administer some painkillers!
        { text = "RM_13e8bd88-0cd4-4e0c-bd76-17d5c684c508", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We'll keep her in for now to get some rest.
	},
};



RecMedia["0aa6bf2d-1596-4d87-abbd-b61196097a4b"] = {
	itemDisplayName = "RM_ff3bdfaf-d654-4ade-a2ac-3141f4c18338", -- VHS: KY Medical Frontline E3
	title = "RM_0c157be1-f321-44e2-8917-ab224e3f4ad6", -- KY Medical Frontline
	subtitle = "RM_2545a7a6-eb49-4276-a220-d5c4070905ff", -- Episode 3: Fractures
	author = nil,
	extra = "RM_b49a7726-8c3d-4689-98c2-0eadff2087e6", -- Ride along with the paramedic team and learn how to treat fractured limbs in this edition of KY Medical Frontline.
	spawning = 0,
	category = "Retail-VHS",
	lines = {
        { text = "RM_1536de5b-09ea-4fec-9c34-fc34d42e7351", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- You are watching, KY Medical Frontline!
        { text = "RM_59f33ecc-113a-4a8b-b7a9-b670a1723f69", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- The time is 01:17am...
        { text = "RM_aca3ea11-8eac-4062-8d0a-7718e0531c90", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- A high-speed disaster, his father's Cossette in pieces...
        { text = "RM_bc2e4c07-78ce-40d8-8aee-864e1a8218f6", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- We arrive with the paramedics to the scene of the accident...
        { text = "RM_67329806-8834-48ea-a240-f31b262b373d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- What's the situation, officer?
        { text = "RM_6d075b9d-2712-4b39-81dc-d62da8b82cf4", r = 0.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- One male. Name - Richard...
        { text = "RM_4bffe1e6-d5a5-418a-a1fd-88633d1dca40", r = 0.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Driving too fast and lost control...
        { text = "RM_2bdb434e-5455-45ef-9daf-2fe00d6c9467", r = 0.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Crashed right into the tree!
        { text = "RM_82c46f42-64b4-4ad7-8eca-816b3e87bcc9", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Thanks, we'll take it from here.
        { text = "RM_495298db-17f8-448b-aae9-6fef5a5bfee2", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- <loud groans>
        { text = "RM_1b04adcb-10e8-4d10-af2b-10c4909eba3e", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Looks like fractures in both arms.
        { text = "RM_6447fc5a-0502-4c0e-85f7-57096669d4e4", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Indeed - and a possible leg fracture too!
        { text = "RM_809a875f-1a5f-4eec-a07c-1c30634d831d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We can't risk moving him until we get them splinted
        { text = "RM_80b37630-2fcd-46bc-a89a-60560f05690b", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- But we only have two splints in the van...
        { text = "RM_e5279f70-cda1-4dca-8baf-704d93381786", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We could always improvise one
        { text = "RM_138352a4-027b-4887-8aee-34fca2ca0047", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Is that advised?
        { text = "RM_a651276f-271d-455d-889c-be334c65bb8a", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- No - but it's better than nothing
        { text = "RM_0f8e07ad-b3f5-4b21-80f5-c64dc10f63cf", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- What do you need then?
        { text = "RM_21901f79-fada-4ca9-9b3f-abb858981b77", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- I need something sturdy and straight...
        { text = "RM_3b9a4f93-87e8-45ca-af75-ee448277e539", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- What about this tree branch?
        { text = "RM_7d3dc355-5b57-4b0a-ba99-cb714ce3411c", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- That'll do, and something to tie it together...
        { text = "RM_c2e2fd91-a8f8-44d1-9e4f-d63f1dc2c0f2", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- We have some rags somewhere in the van...
        { text = "RM_ae25d422-5d76-48eb-8999-dc4ec3ea1f49", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Perfect, tie it together and apply to the patient.
        { text = "RM_a07adebe-bdec-4edf-9df7-1ff797ba07f0", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Grab a bandage too - it will help keep things clean.
        { text = "RM_c149d3b3-4442-47de-8c49-4bb36a7d8ba3", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- That should hold...
        { text = "RM_5030824d-4b60-462c-aae5-d62ed749e5ff", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We can move him onto the stretcher.
        { text = "RM_7855929f-e86f-4f5e-b53e-1d13002db147", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- And where did you learn how to do this?
        { text = "RM_8d1f38ac-4f0c-410c-a247-04e01c624a3a", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Learned this in boy scouts...
        { text = "RM_d4a34c89-05a0-4a5c-83c5-50bc45c599b5", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Really handy if you're out hiking and get into trouble!
        { text = "RM_d8b4a37f-a3ed-4a0f-869d-940673926373", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- <groans>
        { text = "RM_f70e1dc0-cfda-4a0a-9657-e85670d112fb", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Let's get this guy off to the hospital...
        { text = "RM_8869e7a0-c92b-4090-887e-ae953efc3179", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- You think we can cure the speed demon in him too?
        { text = "RM_ddc8f944-59df-457e-8474-4a36b10d24f7", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Haha, Doubt it!
	},
};

RecMedia["f96e8f17-7fcc-4dec-8131-9e215524ea3d"] = {
	itemDisplayName = "RM_dc12d090-a5b5-438d-9173-476e6945decd", -- VHS: KY Medical Frontline E4
	title = "RM_0c157be1-f321-44e2-8917-ab224e3f4ad6", -- KY Medical Frontline
	subtitle = "RM_fa0cceec-f410-4468-8e25-76149b3edd02", -- Episode 4: Burns
	author = nil,
	extra = "RM_c6e46e67-1f17-4f73-a59a-6a0002d2829b", -- Follow Dr. Monroe and staff and learn how to treat burns in this edition of KY Medical Frontline.
	spawning = 0,
	category = "Retail-VHS",
	lines = {
        { text = "RM_1536de5b-09ea-4fec-9c34-fc34d42e7351", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- You are watching, KY Medical Frontline!
        { text = "RM_388c645c-3e90-4dca-9502-64a9c087147c", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- The time is 15:30pm...
        { text = "RM_3d938661-1f0b-4795-ac1d-591d0c1d942d", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- A local mechanic suffers third degree burns to his arm...
        { text = "RM_4afedd24-e56a-47fb-901d-f8ee9da8842d", r = 0.80, g = 0.50, b = 0.00, codes = "BOR-1" }, -- He was dropped off at A&E by a colleague...
        { text = "RM_27da0158-8a6f-45ec-856e-4ca5ffc6fe29", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Your name, sir?
        { text = "RM_eaa92700-af80-4b5a-ad15-32629c5684d7", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Andreas
        { text = "RM_0fce3ea2-011c-4534-b11d-4178f44b0a7c", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- And what happened?
        { text = "RM_73b3f77d-29f9-4ed8-bcb5-3442b1b85661", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Propane tank exploded while I was welding...
        { text = "RM_715d0f7d-2943-46f8-a686-26b9ba09511d", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- I keep telling Carlos to not smoke while I'm working!
        { text = "RM_e744b231-ff57-445f-94aa-1c59883f02a6", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Please lie on the bed, we will see what we can do.
        { text = "RM_fb22f3e9-e5b0-431b-84f0-34d8dd157bde", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- <sigh>
        { text = "RM_e29799a8-817c-4d04-b685-26b05933ac16", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Cleaning burns is always difficult...
        { text = "RM_e4a497fa-68ad-487e-ace8-27bf62df7e47", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Could we use cotton balls and disinfectant instead?
        { text = "RM_86a03300-b34c-48d3-9ecd-e30827bda777", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- I like the way you think, nurse!
        { text = "RM_56112af6-3920-4202-a298-f1d0b7230689", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Let's grab some cotton balls...
        { text = "RM_0aee39a1-9950-474b-aae0-dae80124d458", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Soak them in disinfectant...
        { text = "RM_0f65574e-0f1f-4690-88a2-022746dee0f8", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- And clean the wound that way!
        { text = "RM_2ef2d6f8-2307-4319-8e7e-f4c31c6e1e0d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Now we need to cover this up...
        { text = "RM_f29c72c9-ec3a-4331-9649-222c9063c303", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- Burns are easy to get infected!
        { text = "RM_faebe64c-f36f-422f-a2a7-2db82e91dc4d", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- So, let's use some bandages.
        { text = "RM_93c7331c-f418-4c6f-8b81-0d60af796d1b", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Sterilise them first...
        { text = "RM_34c42968-effe-44d7-8c14-225e00f6294b", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- Cover the burnt areas...
        { text = "RM_245cd3ec-befe-4c66-ae25-ef0d76a2ed75", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- All done.
        { text = "RM_68ac8977-55fc-4cb1-99f5-11c108bbd0d3", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Will I be able to go back to work?
        { text = "RM_a3780b47-728f-42e8-9e02-599209132ae4", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- We recommend taking a couple of weeks off work...
        { text = "RM_24775385-1fee-4feb-b398-a386ddc19367", r = 0.30, g = 0.60, b = 0.80, codes = "BOR-1" }, -- This will take a long time to heal.
        { text = "RM_a7ceefe7-0b1d-4603-942a-8c504fbeae5f", r = 0.30, g = 0.60, b = 0.80, codes = "DOC+1" }, -- You will also need to redress the wounds regularly.
        { text = "RM_75a5bf2d-f756-477d-b3b4-c1d8ef67ee44", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Thanks, doc.
        { text = "RM_70bcbb6e-8878-4e50-ad06-a94281fb69b7", r = 1.00, g = 0.40, b = 1.00, codes = "BOR-1" }, -- Perhaps Carlos should learn safer workplace practices...
        { text = "RM_fb6e72d0-200c-4d08-aead-371c11647b56", r = 0.00, g = 1.00, b = 0.30, codes = "BOR-1" }, -- Ha, You're telling me!
	},
};

